class EmployeeRegister(object):

    def employeeReg(self):
        db=sqlite3.connect("Hospital.db")

        Emp_Id= self.EmpIDLE.text()
        Emp_Name=self.EmpNameLE.text()
        Emp_Address= self.EmpAddLE.text()
        Emp_Email= self.EmpEmailLE.text()
        Emp_Phone_No= self.EmpPhoneLE.text()
        Emp_Age = self.EmpAgeLE.text()
        Emp_Marital_Status = self.MaritalCMB.currentText()
        Emp_Gender=self.GenderCMB.currentText()
        Emp_Job= self.EmpJobCMB.currentText()
        Emp_Salary = self.EmpSalCMB.currentText()

        db.execute('create table if not exists employeeInfo (ID int, Name text, Address text, Email text, Phone int, Age int, Status text, Gender text, Job text, Salary text)')
        db.execute('insert into employeeInfo values (?,?,?,?,?,?,?,?,?,?)', (Emp_Id, Emp_Name,Emp_Address,Emp_Email,Emp_Phone_No,Emp_Age,Emp_Marital_Status,Emp_Gender,Emp_Job,Emp_Salary))
        db.commit()
        message = QMessageBox()
        message.setWindowTitle("Successfull")
        message.setInformativeText("You have successfully registered:  " + Emp_Name)
        message.exec_()
        # Clearing all the fields once it is done
        db.close()

        self.EmpIDLE.clear()
        self.EmpNameLE.clear()
        self.EmpAddLE.clear()
        self.EmpEmailLE.clear()
        self.EmpPhoneLE.clear()
        self.EmpAgeLE.clear()
        self.MaritalCMB.setCurrentText("")
        self.GenderCMB.setCurrentText("")
        self.EmpJobCMB.setCurrentText("")
        self.EmpSalCMB.setCurrentText("")

