from PyQt5 import QtCore, QtGui, QtWidgets
from PyQt5.QtWidgets import QMessageBox

import sqlite3
import cs


class PatientRegster(object):

    def RegisterPatient(self):

        Name = self.PatNameLE.text()
        Address= self.PatAddLE.text()
        Email= self.PatEmailLE.text()
        Phone_no = self.PatPhoneLE.text()
        Date_of_Birth = self.dateEdit.text()
        Marital_Status = self.GenderCMB.currentText()
        Gender= self.GenderCMB.currentText()
        Illness= self.PatSickLE.text()

        db = sqlite3.connect("Hospital.db")
        db.execute("create table if not exists PatientInfo (Name text ,Date_of_Birth int,Address text,Phone_no int,Marital_Status text ,Gender text,Illness text,Email text)")
        db.execute("insert into PatientInfo Values(?,?,?,?,?,?,?,?)", (Name,Date_of_Birth,Address,Phone_no,Marital_Status,Gender,Illness,Email))
        db.commit()

        message = QMessageBox()
        message.setWindowTitle("Registration Succesfull")
        message.setInformativeText(f", Patient {Name} registered succesfull")
        message.exec_()

        # to clear details after registration
        self.PatNameLE.clear()
        self.PatAddLE.clear()
        self.PatEmailLE.clear()
        self.PatPhoneLE.clear()
        self.dateEdit.clear()
        self.GenderCMB.setCurrentText("")
        self.MaritalCMB.setCurrentText("")
        self.PatSickLE.clear()



